let sidebarCart;
let overlay;
let openCart;
let closeCart;
let openOverlay;
let closeOverlay;
let getCart;
let populateProductList;
let clearProductList;
let productList;
let cartEmptyDiv;
let originalCartButton;
let replaceCartButton;
let refreshCart;
let changeQuantity;
let setQuantity;
let setProductListeners;
let typingTimer;
let adjustSpanAndInput;
let updateShippingText;
let freeShippingAmount = 40;

function getRequest(url) {
  let result;
  fetch(url)
    .then((response) => {
      response.text();
    })
    .then((text) => {
      result = text;
    });
  return result;
}

function numberWithCommas(x) {
  x = x.toString();
  var pattern = /(-?\d+)(\d{3})/;
  while (pattern.test(x)) x = x.replace(pattern, "$1,$2");
  return x;
}

function mainJS() {
  originalCartButton = document.querySelector(`a[href="/cart"]`);

  document.head.insertAdjacentHTML("beforeend", cartSTYLE);
  document.body.insertAdjacentHTML("beforeend", cartHTML);

  sidebarCart = document.querySelector("#twik-sidebar-cart");
  overlay = document.querySelector("#twik-overlay");
  productList = document.querySelector(".twik-cart-product-list");
  cartEmptyDiv = document.querySelector(".twik-cart-empty");

  openCart = () => {
    $(sidebarCart).animate({
      right: "0px",
    });
  };
  closeCart = () => {
    $(sidebarCart).animate({
      right: "-1000px",
    });
  };
  openOverlay = () => {
    $(overlay).fadeIn();
  };
  closeOverlay = () => {
    $(overlay).fadeOut();
  };

  getCart = () => {
    return JSON.parse(Get("/cart.json"));
  };

  clearAndHideProductList = () => {
    productList.innerHTML = "";
    $(productList).hide();
    $(".twik-cart-footer").css({ visibility: "hidden" });
    $(cartEmptyDiv).show();
    updateShippingText(0);
  };

  setQuantity = (key, newQuantity) => {
    let json = getCart();
    for (let item of json["items"]) {
      if (item["key"] === key) {
        fetch("/cart/change.js", {
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ id: key, quantity: newQuantity }),
          method: "POST",
        })
          .then(() => {
            refreshCart();
          })
          .catch((error) => {
            console.log(`Twik post error: ${error}`);
          });
      }
    }
  };

  // setQuantity("31373037207636:a2f6a1e08d3b069a7077ab397d43ef2d", 2)

  changeQuantity = (key, change) => {
    let json = getCart();
    for (let item of json["items"]) {
      if (item["key"] === key) {
        let newQuantity = parseInt(item["quantity"]) + change;
        fetch("/cart/change.js", {
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ id: key, quantity: newQuantity }),
          method: "POST",
        })
          .then(() => {
            refreshCart();
          })
          .catch((error) => {
            console.log(`Twik post error: ${error}`);
          });
      }
    }
  };

  populateProductList = () => {
    let cartJSON = getCart();
    let currency = cartJSON["currency"];
    let symbol = currencyJSON[currency]["symbol"];
    let requiresShipping = cartJSON["requires_shipping"];
    let subtotal_price_CENTS = cartJSON["items_subtotal_price"];
    let total_price_CENTS = cartJSON["total_price"];
    let total_discount_CENTS = cartJSON["total_discount"];
    let total_weight = cartJSON["total_weight"];
    let item_count = cartJSON["item_count"];
    let items = cartJSON["items"];

    for (let item of items) {
      document.querySelector(
        ".twik-cart-subtotal"
      ).innerHTML = `${symbol}${numberWithCommas(total_price_CENTS / 100)}`;

      $(cartEmptyDiv).hide();
      $(".twik-cart-footer").css({ visibility: "visible" });
      $(productList).show();

      productList.insertAdjacentHTML(
        "beforeend",
        `
        <div class="twik-cart-product">
            <div class="twik-cart-image-wrapper">
                <a href="${item["url"]}">
                <img src="${
                  item["image"]
                }" alt="product-image" class="twik-cart-image">
                </a>
            </div>
            <div class="twik-cart-product-details">
                <a href="${item["url"]}">
                    <div class="twik-cart-details-header">
                        ${item["product_title"]}
                        </div>
                    </a>
                <div class="twik-cart-details-subheader">${
                  item["variant_title"] ? item["variant_title"] : ""
                }</div>
                <div class="twik-cart-price">${symbol}${numberWithCommas(
          item["final_price"] / 100
        )}</div>
                <div class="twik-cart-product-quantity-container">
                    <div class="twik-cart-product-quantity-wrapper">
                        <div class="twik-quantity-border-div">
                            <div class="twik-minus-one" data-key="${
                              item["key"]
                            }">-</div>
                            <input class="twik-cart-product-quantity-input" type="number" data-key="${
                              item["key"]
                            }" value=${item["quantity"]}>
                            <span class="twik-cart-product-quantity-span"></span>
                            <div class="twik-plus-one" data-key="${
                              item["key"]
                            }">+</div>
                        </div>
                        <div class="twik-remove-item" data-key="${
                          item["key"]
                        }"><div>Remove</div></div>
                    </div>
                </div>
            </div>
        </div>
        `
      );

      let input = document.querySelectorAll(
        ".twik-cart-product-quantity-input"
      )[
        document.querySelectorAll(".twik-cart-product-quantity-input").length -
          1
      ];
      let span = document.querySelectorAll(".twik-cart-product-quantity-span")[
        document.querySelectorAll(".twik-cart-product-quantity-span").length - 1
      ];

      span.innerHTML = input.value.replace(/\s/g, "&nbsp;");
      adjustSpanAndInput(span, input);
      updateShippingText(total_price_CENTS / 100);
    }
  };

  adjustSpanAndInput = (span, input) => {
    if (span.offsetWidth <= 60 && span.offsetWidth >= 25) {
      input.style.width = span.offsetWidth + 5 + "px";
    } else if (span.offsetWidth > 60) {
      input.style.width = 65 + "px";
    }
  };

  refreshCart = () => {
    clearAndHideProductList();
    populateProductList();
    if (document.querySelectorAll(".twik-cart-product").length === 0) {
      clearAndHideProductList();
      return;
    }
    setProductListeners();
  };

  replaceCartButton = () => {
    let cloneCartButton = originalCartButton.cloneNode(true);
    cloneCartButton.setAttribute("data-action", "");
    cloneCartButton.setAttribute("onclick", "");
    cloneCartButton.addEventListener("click", (e) => {
      e.preventDefault();
      refreshCart();
      openOverlay();
      openCart();
    });
    originalCartButton.replaceWith(cloneCartButton);
    cloneCartButton.parentElement.addEventListener("click", (e) => {
      e.stopPropagation();
    });
  };

  updateShippingText = (currentTotal) => {
    document.querySelector(".twik-cart-shipping").innerText =
      currentTotal < freeShippingAmount
        ? `Spend €${
            freeShippingAmount - currentTotal
          } more and get free shipping!`
        : `You are eligible for free shipping!`;
  };

  setProductListeners = () => {
    document.querySelectorAll(".twik-plus-one").forEach((btn) => {
      btn.addEventListener("click", (e) => {
        e.preventDefault();
        changeQuantity(btn.getAttribute("data-key"), 1);
      });
    });

    document.querySelectorAll(".twik-minus-one").forEach((btn) => {
      btn.addEventListener("click", (e) => {
        e.preventDefault();
        changeQuantity(btn.getAttribute("data-key"), -1);
      });
    });

    document.querySelectorAll(".twik-remove-item").forEach((btn) => {
      btn.addEventListener("click", (e) => {
        e.preventDefault();
        setQuantity(btn.getAttribute("data-key"), 0);
      });
    });

    document
      .querySelectorAll(".twik-cart-product-quantity-input")
      .forEach((input) => {
        input.addEventListener("keydown", () => {
          clearTimeout(typingTimer);
          typingTimer = setTimeout(() => {
            setQuantity(input.getAttribute("data-key"), parseInt(input.value));
          }, 1000);
        });
        let span = input.parentElement.querySelector(
          ".twik-cart-product-quantity-span"
        );
        input.addEventListener("input", () => {
          span.innerHTML = input.value.replace(/\s/g, "&nbsp;");
          adjustSpanAndInput(span, input);
        });
      });
  };

  window.addEventListener("click", () => {
    closeCart();
    closeOverlay();
  });
  document
    .querySelector("#twik-sidebar-cart-wrapper")
    .addEventListener("click", (e) => {
      e.stopPropagation();
    });
  overlay.addEventListener("click", () => {
    closeCart();
    closeOverlay();
  });
  document
    .querySelector(".twik-cart-close-wrapper")
    .addEventListener("click", () => {
      closeCart();
      closeOverlay();
    });

  replaceCartButton();
}
if (window.jQuery) {
  $ = window.jQuery;
  mainJS();
} else {
  var script = document.createElement("SCRIPT");
  script.src =
    "https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js";
  script.type = "text/javascript";
  // this is doc.ready
  //-------------------
  script.onload = function () {
    var $ = window.jQuery;
    mainJS();
  };
  document.getElementsByTagName("head")[0].appendChild(script);
}

