function addToCartButtonClick(prod, data_url) {
  prod.querySelector(".twik-add-to-cart-btn").style.display = "none";
  prod.querySelector(".twik-adding-message").style.display = "block";

  fetch(data_url)
    .then((response) => {
      return response.json();
    })
    .then((res) => {
      find_and_add_product(res);
      prod.querySelector(".twik-adding-message").style.display = "none";
      prod.querySelector(".twik-thankyou-message").style.display = "block";
      setTimeout(() => {
        prod.querySelector(".twik-thankyou-message").style.display = "none";
        prod.querySelector(".twik-add-to-cart-btn").style.display = "block";
      }, 1000);
    })
    .catch((error) => {
      console.log(`add to cart POST request failed; error: ${error}`);
      setTimeout(() => {
        prod.querySelector(".twik-adding-message").style.display = "none";
        prod.querySelector(".twik-add-to-cart-btn").style.display = "block";
      }, 1000);
    });
}

// given a product, finds the url for the JSON representation of the product page
function create_data_finding_url(selector, item_num) {
  let prod = document.querySelectorAll(selector)[item_num];
  let prod_str = prod.innerHTML;
  let href;

  let re = /href=".+"/gi;
  if (re.test(prod_str) == true) {
    href = prod_str.match(re)[0];
    if (href.indexOf(" ") != -1) {
      href = href.substring(0, href.indexOf(" "));
    }
  }

  // removes gibberish from url:
  href = href.substring(href.indexOf('"') + 1, href.lastIndexOf('"'));

  if (href.indexOf(".co.") != -1) {
    href = href.substring(href.indexOf("/"), href.length);
  }

  if (href.indexOf("?") != -1) {
    href = href.substring(0, href.indexOf("?"));
  }

  href = href + ".json";
  return href;
}

// sort through product data for variant id and then trigger addtc
function find_and_add_product(json_data) {
  let variant_id = json_data["product"]["variants"][0]["id"];

  fetch(`/cart/add.js?id=${variant_id}&quantity=1`, {
    method: "POST",
  })
    .then((response) => {
      if (response.ok) {
        // document.location.reload();
        refreshCart();
        openCart();
      }
    })
    .catch((error) => {
      console.log(`error: ${error}`);
    });
}

